{"windows":"// Place your key bindings in this file to override the defaultsauto[]
// open search editor at current hierarchy
[
    {
        "key": "ctrl+s f",
        "command": "search.action.openNewEditor",
        "args": {
            "query": "",
            "triggerSearch": true,
            "focusResults": false,
            "includes": "${fileBasenameNoExtension}",
            "regexp": true,
            "showIncludesExcludes": true
        }
    },
    {
        "key": "ctrl+alt+b",
        "command": "workbench.action.toggleSidebarVisibility"
    },
    // look in *
    {
        "key": "ctrl+l l",
        "command": "dendron.lookupNote",
        "args": {
            "initialValue": ""
        }
    },
    // look in ppl.*
    {
        "key": "ctrl+l p",
        "command": "dendron.lookupNote",
        "args": {
            "initialValue": "ppl.*"
        }
    },
    // look in co.*
    {
        "key": "ctrl+l c",
        "command": "dendron.lookupNote",
        "args": {
            "initialValue": "co.*"
        }
    },
    //create scractch note from highlight
    {
        "key": "ctrl+s s",
        "command": "dendron.lookupNote",
        "args": {
            "noteType": "scratch",
            "selectionType": "selection2link",
            "noConfirm": true
        }
    },
    // --- Search
    // Look across all open tasks
    // NOTE: the month field needs to manually be kept up to date
    {
        "key": "ctrl+l t",
        "command": "search.action.openNewEditor",
        "args": {
            "query": "",
            "triggerSearch": false,
            "focusResults": false,
            "isRegexp": true,
            "includes": "daily.journal.202*"
        }
    },
    { // look up note, then open it to the side. also copy selected note's link.
        "key": "ctrl+l .",
        "command": "dendron.lookupNote",
        "args": {
          "splitType": "horizontal",
          "copyNoteLink": true
        }
      },
    // Insert note
  {
    "key": "ctrl+s i",
    "command": "dendron.insertNote"
  },
    // --- Search
    // Show All Edirtors
    {
        "key": "ctrl+t",
        "command": "workbench.action.showAllEditors"
    },
    {
        "key": "alt+\\",
        "command": "workbench.action.splitEditor"
    },
    {
        "key": "ctrl+\\",
        "command": "-workbench.action.splitEditor"
    }
]","mac":"[
    {
        "key": "ctrl+cmd+l",
        "command": "dendron.lookupNote",
        "when": "dendron:pluginActive"
    },
    {
        "key": "cmd+l",
        "command": "-dendron.lookupNote",
        "when": "dendron:pluginActive"
    }
]"}
